using C=System.Console;using System.Linq;class H{public static void Main(){int i,j,n=int.Parse(C.ReadLine());string s="",t="",c="",z,r;var w=new string[n];for(i=0;i<n;i++)w[i]=C.ReadLine();System.Array.Sort(w);for(i=0;i<n;i++){z=w[i];if(z!=""){r=new string(z.Reverse().ToArray());for(j=i+1;j<n;j++){if(w[j]==r){s+=z;t=r+t;w[j]="";j=n;}}if(j==n&&z==r&&(c==""||c.CompareTo(r)>0))c=r;}}C.WriteLine(s+c+t);}}
